package main

import (
	"log"
	"testing"
)

func TestPasswdCreate(t *testing.T) {
	hash, err := CreatePasswdHash([]byte("haha"))
	if err != nil {
		log.Panicln(err)
	}
	log.Println(string(hash))
}

func TestPasswdVerify(t *testing.T) {
	hash, err := CreatePasswdHash([]byte("haha"))
	if err != nil {
		log.Panicln(err)
	}
	log.Println(VerifyPasswdHash(hash, []byte("haha")))
	log.Println(VerifyPasswdHash(hash, []byte("hahax")))
	log.Println(VerifyPasswdHash([]byte("AA=="), []byte("hahax")))
	log.Println(VerifyPasswdHash(nil, []byte("hahax")))
	log.Println(VerifyPasswdHash([]byte(""), []byte("hahax")))
}
